select
, listRelation.list_id from fruits_task task right join ( select task_id,list_id from task_list_relation where list_id in (select list_id from fruits_list flist inner join (select list_id from project_list_relation where project_id = #{projectId} and is_deleted = 'N') plr on plr.list_id = flist.uuid and
AND flist.is_deleted = 'N'
) and is_deleted = 'N' ) listRelation on listRelation.task_id = task.uuid
AND task.is_deleted = 'N' and task.uuid is not null
order by ${example.orderByClause}

select
,taskJoin.task_id from fruits_plan fplan right join (select task_id,plan_id from task_plan_relation where is_deleted = 'N') taskJoin on taskJoin.plan_id = fplan.uuid right join (select ftask.uuid from fruits_task ftask
and ftask.is_deleted = 'N'
) ftask on ftask.uuid = taskJoin.task_id where fplan.uuid is not null and fplan.is_deleted = 'N';

select
, userTask.user_role, userTask.uuid as task_id from ( SELECT userJoin.user_id,task.uuid,userJoin.user_role from ( select
from fruits_task ftask right join (select task_id,project_id from task_project_relation
project_id = #{projectId}
and is_deleted = 'N'
) projectJoin on projectJoin.task_id = ftask.uuid where ftask.is_deleted = 'N' union all select
from fruits_task ftask right join (select plan_id,task_id from task_plan_relation where is_deleted = 'N') taskJoin on taskJoin.task_id = ftask.uuid right join (select plan_id,project_id from plan_project_relation
project_id = #{projectId}
and is_deleted = 'N'
) planJoin on planJoin.plan_id = taskJoin.plan_id where ftask.is_deleted = 'N' ) task left join (select uuid,task_id,user_id,user_role from task_user_relation
user_id in
#{id}
and is_deleted = 'N'
) userJoin on userJoin.task_id = task.uuid
and task.uuid is not null
group by userJoin.user_id,task.uuid,userJoin.user_role ) userTask left join (select
from fruits_user fuser where fuser.is_deleted = 'N' ) fuser on fuser.user_id = userTask.user_id where fuser.user_id is not null

